this.calculate = function ( longtext, action, language, textoutput ) {
var letters = language["letters"];
if (action == "c") {
	var t = longtext.toLowerCase();
	var outt = "";
	var needseparator = false;
	for (var i = 0, len = t.length; i < len; i++) {
	  	var l = t[i];
		var idx = letters.indexOf(l);
		if (-1 == idx) {
			outt += l;
			needseparator = false;
		}
		else { 
			if (needseparator) outt += "-";
			outt += (idx+1).toString();
			needseparator = true;
		}
	}
	textoutput.SetValue(outt);
} else if (action == "d") {
	var t = longtext.replace(/([\d]{1,2})([^\d]|$)/g, function(match, p1, p2) {
		return letters[Number(p1)-1] + (p2 == "-" ? "" : p2);
	});
	textoutput.SetValue(t);
}
};};
